INSERT INTO `products` (`product_id`, `product_name`, `category`, `description`, `product_sku`, `qty_stock`, `cost`, `price`, `image_url`, `sale`, `featured`, `new`) VALUES
(2, 'Bear Paws&reg; Meat Handler Forks', 'kitchen', 'Lift and carve hot food with these dishwasher safe bear claws. Shred meat and chicken. Great to use for outdoor grilling or in the kitchen. Stay cool handles', 952578012586, 50, '10.93 ', '19.99 ', 'http://ecx.images-amazon.com/images/I/61evMlc%2BApL._SL1024_.jpg', '', '', 'yes'),
(3, 'DCI BBQ Personalized Branding Iron', 'kitchen', 'Metal branding iron with wooden handle. Includes every letter of the alphabet, plus extra letters and spacers. Easy to use--just slide letters onto the letter track, no tools needed. Hand wash in soapy water after use.', 876201242412, 40, '15.25 ', '23.99 ', 'http://ecx.images-amazon.com/images/I/61k9xSKQVOL._SL1125_.jpg', '', '', 'yes'),
(4, 'Stylish Gun Shaped Silicone Ice Tray', 'kitchen', 'This Stylish Gun Shaped tray makes Cubes that pop out easily. Also can be used as a mold for Jell-O, Candy, Chocolate, and more.', 584658215985, 150, '0.92 ', '3.99 ', 'http://ecx.images-amazon.com/images/I/61S3PJOHxNL._SL1500_.jpg', '', '', ''),
(5, 'Bormioli Rocco Selecta Decanter', 'kitchen', 'Italian-made of stunningly cut clear glass with a light-reflecting pattern and rounded edges. Narrow neck and matching squared stopper. Steady base and flared sides. Safe for use in the dishwasher.', 523025478469, 30, '6.26 ', '11.99 ', 'http://ecx.images-amazon.com/images/I/91e8KidEDsL._SL1500_.jpg', '', '', ''),
(6, 'Bluetooth Speaker Showerhead', 'bathroom', 'Magnetic waterproof Bluetooth speaker pops in and out of the showerhead for easy recharging. Premium crisp clean sound / Connects wirelessly up to 33 feet away / Easy & Fast installation. Charges via USB (Cable included) / Built-in rechargeable Battery for up to 10 hours of playtime', 251325202478, 45, '25.60 ', '37.99 ', 'http://ecx.images-amazon.com/images/I/71bUXMJbhZL._SL1500_.jpg', '', 'yes', ''),
(7, 'Bikini Girl Glass Neon Sign', 'electronic', '18''''x 14''''(metal frame), Large neon bikini girl bar sign. Made by senior master benders.', 350248790258, 20, '52.60 ', '89.99 ', 'http://ecx.images-amazon.com/images/I/41NIqY1jrbL.jpg', '', '', 'yes'),
(8, 'Crown Royal&reg; Glass Neon Sign', 'electronic', '18''''x 14''''(metal frame), Large neon Crown Royal bar sign. Made by senior master benders.', 589632154789, 20, '52.60 ', '89.99 ', 'http://ecx.images-amazon.com/images/I/71i11q74jhL._SL1000_.jpg', '', '', ''),
(9, 'Faux Animal Skin Hide Rug (grey)', 'bedroom', '2'' x 4'' Faux animal skin rug. Easy to clean, elegant, can be put in any room.', 254712548721, 25, '36.94 ', '50.00 ', 'http://ecx.images-amazon.com/images/I/91QUH5EsGiL._SL1500_.jpg', '', '', ''),
(10, 'Faux Animal Skin Hide Rug (black)', 'bedroom', '2'' x 4'' Faux animal skin rug. Easy to clean, elegant, can be put in any room.', 254712548722, 25, '36.94 ', '50.00 ', 'http://ecx.images-amazon.com/images/I/414NNXlZc4L.jpg', '', '', ''),
(11, 'Faux Animal Skin Hide Rug (brown)', 'bedroom', '2'' x 4'' Faux animal skin rug. Easy to clean, elegant, can be put in any room.', 254712548723, 25, '36.94 ', '50.00 ', 'http://ecx.images-amazon.com/images/I/414NNXlZc4L.jpg', '', '', ''),
(12, 'The Ex 5-Piece Knife Set', 'kitchen', 'Designed by Raffaele Iannello, this unique 5-piece knife set with holder adds a touch of whimsy to any kitchen. Includes an 8-inch Chef''s knife, 8-inch bread knife, 8-inch carver, 5-inch utility knife, 3-1/2-inch parer, and ABS plastic storage figurine', 658963332512, 15, '109.26 ', '149.99 ', 'http://ecx.images-amazon.com/images/I/41646jpgVeL.jpg', '', 'yes', ''),
(13, 'Knuckle Duster Porcelain Mug', 'kitchen', '', 548698532458, 50, '11.99', '16.99', 'http://ecx.images-amazon.com/images/I/413zbn8qobL.jpg', 'yes', '', ''),
(14, 'Coolest Beer Ever!', 'kitchen', 'kdfnpa fednpi fnsadif npds n fnsdi  nfdsn ifsdnf nsd if nsdinfnpisd pnf npsd npnfp npsd npf pnsd npf pnsan pd pf nsd np', 215469856965, 56, '8.99', '11.99', 'http://ecx.images-amazon.com/images/I/61qTwZquVJL._SL1056_.jpg', '', '', ''),
(15, 'Gray Loveseat', 'furniture', 'Whether plopping down after a long day at work, settling in with coffee and brunch, or entering a spirited discussion with friends, the Engage sofa is a welcome presence in your bachelor pad. \r\nSofa Dimensions: 90.5"L x 33"W x 32.5"H', 122321212567, 14, '500.00', '999.99', 'http://www.beyondstores.com/images/DO/Modway-Engage-Loveseat-And-Sofa-Set-Of-2-In-Gray-01.jpg', '', '', ''),
(16, 'Globe Bookends', 'bedroom', 'Need to look like you read? Add few books you''ll never read to a shelf and hold them up with the Globes and the ladies will swoon!', 867546379234, 23, '50.00', '99.99', 'http://www.woodessen.com/images/product/zoom/0975N-lg.gif', 'yes', '', ''),
(17, 'Carriage Clock', 'bedroom', 'A clock to withstand anything. Carriage Clocks were used during the 1800''s and were kept in Carriages to keep track of time during travel.', 849372837634, 11, '25.00', '59.99', 'http://cdn.notonthehighstreet.com/system/product_images/images/001/527/011/original_royal-mail-carriage-clock.jpg', '', 'yes', ''),
(18, 'Brushed Stainless Steel Ice Bucket', 'kitchen', 'Keep your drinks cold while you try to woo a girl for the night.', 289384756021, 4, '40.00', '79.99', 'http://cdn.shopify.com/s/files/1/0185/7358/products/Photo14_300dpi_1024x1024.jpg?v=1405553450', '', '', ''),
(19, 'Stainless Steel Coffee Table', 'furniture', 'Stainless steel table with dual layers to create a sleek, modern appeal.', 948372643521, 8, '200.00', '399.99', 'http://www.designinsiderlive.com/wp-content/uploads/2014/03/Weave-Coffee-Table.jpg', '', '', ''),
(20, 'Dual Rotor RC Helicopter', 'electronic', 'Smooth Flying Helicopter that flies up and down and left and right. Can be flown freely without interference of other RC Vehicles.', 394857621928, 9, '44.00', '119.99', 'http://cdn1.sharperimage.com/si/img/productImages/201765/201765-p1.jpg', '', '', ''),
(21, 'Poker Table', 'livingroom', '48" Poker Table w/Carrying Bag', 3829387648392, 3, '40.00', '97.99', 'http://ecx.images-amazon.com/images/I/413BgWnjmmL._SY355_.jpg', '', '', ''),
(39, 'Love Seat', '', 'This modern black leather Love seat is the perfect center piece for any bachelor pad living room!', 7458962134, 6, '699', '899.99', 'https://cdn.shopify.com/s/files/1/0185/7358/products/Black_love_seat_copy_6d1cc69e-9343-4c28-86fa-c93be76160b5_1024x1024.jpg?v=1381371079', 'No', 'No', 'No'),
(40, 'Couch', '', 'Black leather couch that is comfortable enough to take a home in any bachelors living room.', 4521697823, 8, '899', '1100.99', 'https://cdn.shopify.com/s/files/1/0185/7358/products/EEI-701-BLK_1_231ed634-c821-4402-9c70-90603d64810a_1024x1024.jpg?v=1381373323', 'No', 'No', 'No'),
(41, 'Coffee table', '', 'This low profile wood coffee table with its dark walnut finish works well with a lot of modern furniture.  The table has  plenty of space to comfortably put your feet up, or have dinner on (use coasters for drinks).  Remember a coffee table is a must have bachelor pad piece of furni', 4521369875, 5, '399', '499.99', 'https://cdn.shopify.com/s/files/1/0185/7358/products/CT621-2_74b065d4-3533-4963-8086-84de66678ec7_1024x1024.jpg?v=1381864878', 'No', 'No', 'No'),
(42, 'EARLY DRIVE II', '', 'A really serene landscape that conveys that magical time when driving as the sun is rising and your only the vehicle on the road.  This canvass is ideal for pairing with with mid-century or drifter styles of furniture.  It communicates that its owner is confident about the art he di', 563218954, 3, '299', '359', 'https://cdn.shopify.com/s/files/1/0185/7358/products/10_1024x1024.jpg?v=1375387176', 'No', 'No', 'No');
